/* */
$(document).ready(function () {
    // Ocultar elemento al cargar la página
    $(".wrapper1").hide();

    // Animación personalizada al hacer clic en "Ocultar Elemento"
    $("#ocultarElemento").click(function() {
        $(".wrapper1").hide({ height: 'toggle', opacity: 'toggle' }, 'slow');
    });

    // Animación personalizada al hacer clic en "Mostrar Elemento"
    $("#mostrarElemento").click(function() {
        $(".wrapper1").show({ height: 'toggle', opacity: 'toggle' }, 'slow');
    });
});


